class Solution {
public:
    Solution(vector<int> nums) {
        this->nums=nums;
    }
    
    int pick(int target) {
        int index;
        int cnt=0;
        for(int i=0; i<nums.size(); ++i) {
            if(nums[i]==target) {
                cnt++;
                if(rand()%cnt==0) index=i;
            }
        }
        
        return index;
    }
private:
    vector<int> nums;
};

